<?php get_template_part( 'part-header' ); ?><?php $taxonomy = 'link_cat'; ?><?php $queried_term = get_query_var($taxonomy); ?><?php $terms = get_the_terms($post->ID, $taxonomy); ?><?php $terms = get_terms($taxonomy, 'slug='.$queried_term.'&order=ASC&orderby=name'  ); ?><?php foreach ($terms as $term) : ?><?php global $post; ?><?php $args = array( 'post_type' => 'link',  'posts_per_page' => 99, 'link_cat' => $term->name ); ?><?php $loop = new WP_Query( $args ); ?><section><div class="sw"><div class="cw tx-c"><div><h3 class="txa-6 txb-6 txc-6 mb1"><?php echo $term->name; ?></h3><?php while ( $loop->have_posts() ) : $loop->the_post(); ?><?php $link_url = get_post_meta(get_the_ID(), "_poxy_link_url", true); ?><?php if ($link_url) : ?><p class="rel txa-4 txb-4 txc-4 txd-4 txe-4 txc-4 mt2"><?php poxy_edit(); ?><a href="<?php echo $link_url; ?>" target="_blank"><?php the_title(); ?></a></p><?php else : ?><p class="rel txa-4 txb-4 txc-4 txd-4 txe-4 txc-4 mt2"><?php poxy_edit(); ?><?php the_title(); ?></p><?php endif; ?><p class="rel txa-1 txb-1 txc-1 txd-1 txe-1 mt0"><?php echo get_the_content(); ?></p><?php endwhile; ?><?php wp_reset_query(); ?></div></div></div></section><?php endforeach; ?>