<?php get_template_part( 'mixin-header' ); ?><section><div class="sw"><div class="cw"><select ng-model="clientgrid" ng-options="clientgrid.size as clientgrid.name for clientgrid in clientgrids" class="a18 form-control mb4"></select></div></div></section><?php $taxonomy = 'project_client'; ?><?php $queried_term = get_query_var($taxonomy); ?><?php $args = array( 'orderby' => 'term_order', 'hide_empty' => true, 'parent' => 0 ); ?><?php $cats = get_terms($taxonomy, $args); ?><section class="{{clientgrid}}"><div class="sw"><div id="thumbs" class="cw thumbs waterfall"><?php foreach ($cats as $cat) : ?><?php $term_id = $cat->term_id; ?><?php $tax_meta = get_option( "taxonomy_term_$term_id" ); ?><?php if(isset($tax_meta['cat_color_red']) && isset($tax_meta['cat_color_green']) && isset($tax_meta['cat_color_blue'])) : ?><?php $red = $tax_meta['cat_color_red']; ?><?php $green = $tax_meta['cat_color_green']; ?><?php $blue = $tax_meta['cat_color_blue']; ?><?php $background_color = 'background-color: rgba('.$red.','.$green.','.$blue.', .6)'; ?><?php else : ?><?php $background_color = 'background-color: rgba(150,150,150, .9)'; ?><?php endif; ?><?php $link = get_term_link( $cat->slug, 'project_client' ); ?><?php $id = $cat->term_id; ?><?php $title = $cat->name; ?><?php $description = $cat->description; ?><?php $slug = $cat->slug; ?><?php $term_id = $cat->slug; ?><?php $image_url = poxy_tax_image($term_id); ?><?php if ($image_url) : ?><?php $image = 'background-image: url('. $image_url. ')'; ?><?php else : ?><?php $image_ph = get_bloginfo('template_url') . '/images/placeholder-logo-white.svg'; ?><?php $image = 'background-image: url('.$image_ph.')'; ?><?php endif; ?><?php $slide_image_url = get_bloginfo("url") . '/images/thumb-slide-test.png'; ?><?php $slide_image = 'background-image: url('.$slide_image_url.')'; ?><figure ng-class="{ fadeIn : waypoint<?php echo $id; ?>s.flags.off }" class="op0 br2"><div zum-waypoint="waypoint<?php echo $id; ?>s" down="flags.off" offset="80%"><a href="<?php echo $link; ?>" rel="bookmark"><span class="oxy inside-bevel br2"></span><span style="<?php echo $background_color; ?>" class="accent-border oxy br2 fill-95"></span><span style="<?php echo $background_color; ?>" class="highlight-border oxy br2 fill-95"></span><span class="thumb oxy ofh br2 bg-contain bgp-cc"><span style="<?php echo $image; ?>" class="icon _oxya18a_18 _oxyb110b_110 oxy ofh bg-contain bgp-cc"></span></span><div class="shadow oxy shadow ofh br2"></div><div class="title txa-2 txb-2 tx-c oxy6 ap100 bp100 cp100 dp100 cp100 z4 mb1"><?php echo $title; ?></div></a></div></figure><?php endforeach; ?></div></div></section>