<?php get_template_part( 'part-header' ); ?><?php $taxonomy = 'setting_cat'; ?><?php $queried_term = get_query_var($taxonomy); ?><?php $terms = get_the_terms($post->ID, $taxonomy); ?><?php $terms = get_terms($taxonomy, 'slug='.$queried_term  ); ?><?php foreach ($terms as $term) : ?><section><div class="sw"><div class="cw"><div class="poxy a14b14"><h1 class="bold fs___6"><?php echo $term->name; ?></h1><p class="fs___2 poxy a15b15"><?php echo $term->description; ?></p></div><div class="poxy a34b34"><?php $args = array( 'post_type' => 'setting',  'posts_per_page' => 99, 'setting_cat' => $term->slug ); ?><?php $loop = new WP_Query( $args ); ?><?php while ( $loop->have_posts() ) : $loop->the_post(); ?><?php global $post; ?><?php $id = $post->ID; ?><?php $example = get_post_meta($id, 'poxy_example', true); ?><?php $example = $example ? $example : false; ?><?php $important = get_post_meta($id, 'poxy_important', true); ?><?php $important = $important ? $important : false; ?><?php $options = get_post_meta($id, 'poxy_options', true); ?><?php $options = $options ? $options : false; ?><?php $var_type = get_post_meta($id, 'poxy_var_type', true); ?><?php $var_type = $var_type ? $var_type : false; ?><?php $default_val = get_post_meta($id, 'poxy_default_val', true); ?><?php $default_val = $default_val ? $default_val : false; ?><h2 class="poxy p100 fs___5 bold"><?php the_title(); ?></h2><article class="wrap"><div class="poxy a13b13 mt4"><div class="a14b14"><?php the_content(); ?><?php if ($example) : ?><div class="poxy bold mt2 mb2 p100 fs___4"><?php echo $example; ?></div><?php endif; ?><?php if ($var_type) : ?><div class="poxy p100"><span class="bold mt4 mr4">Datatype:</span><?php echo $var_type; ?></div><?php endif; ?><?php if ($options) : ?><div class="poxy p100"><span class="bold mt4 mr4">Options:</span><?php echo $options; ?></div><?php endif; ?></div></div><div class="poxy a13b13 mt4"><?php if ($important) : ?><p class="fs___2 poxy"><span class="accent">*</span><?php echo $important; ?></p><?php endif; ?></div></article><div class="poxy p100 mb1"><div class="divider bgc__dark__10 poxy a14b14"></div></div><?php endwhile; ?><?php wp_reset_query(); ?></div></div></div></section><?php endforeach; ?>